package arr4;

public class arr4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char[] letters = {'a', 'b', 'c', 'd', 'e'};
        
        // Use a do-while loop to print out each element of the array
        int i = 0;
        do {
            System.out.print(letters[i]+" ");
            i++;
        } while (i < letters.length);

	}

}
